import React, { useState, useEffect, useRef } from 'react';
import {
  LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, AreaChart, Area, BarChart, Bar
} from 'recharts';
import {
  Brain, TrendingUp, TrendingDown, DollarSign, BarChart3, AlertTriangle, CheckCircle, Eye, Zap, Target, Settings, Bell, Database, Activity, Pause, Play, RotateCcw
} from 'lucide-react';

const TradingDashboard = () => {
  const [isSystemRunning, setIsSystemRunning] = useState(false);
  const [connectionStatus, setConnectionStatus] = useState('disconnected');
  type RealTimeDataItem = {
    price: number;
    change: number;
    volume: number;
    signal: string;
    confidence: number;
    mlConfidence: number;
    technicalScore: number;
    sentimentScore: number;
    riskLevel: string;
    lastUpdate: string;
  };
  
  type RealTimeData = {
    [pair: string]: RealTimeDataItem;
  };
  
  const [realTimeData, setRealTimeData] = useState<RealTimeData>({});
  const [trades, setTrades] = useState<Trade[]>([]);
  type Trade = {
    id: number;
    entryPrice: number;
    pair: string;
    signal: string;
    lotSize: number;
    confidence: number;
    timestamp: string;
    status: string;
    pnl?: string;
  };

  type Performance = {
    totalTrades: number;
    winningTrades: number;
    totalProfit: number;
    winRate: number;
  };

  type Notification = {
    id: number;
    type: 'info' | 'success' | 'error' | 'warning';
    message: string;
    timestamp: string;
  };

  const [performance, setPerformance] = useState<Performance>({
    totalTrades: 0,
    winningTrades: 0,
    totalProfit: 0,
    winRate: 0,
  });
  const [mlModelStatus, setMlModelStatus] = useState({ trained: false, accuracy: 0 });
  const [notifications, setNotifications] = useState<Notification[]>([]);
  const [settings, setSettings] = useState({
    riskPerTrade: 1.5,
    maxDailyTrades: 8,
    minConfidence: 75,
    symbols: ['XAUUSD', 'EURUSD', 'GBPUSD', 'BTCUSD'],
    notifications: { telegram: false, discord: false }
  });

  const tradingPairs = settings.symbols;
  const websocket = useRef(null);

  // Simular conexiÃ³n WebSocket para datos en tiempo real
  useEffect(() => {
    if (isSystemRunning) {
      const interval = setInterval(() => {
        const newData: RealTimeData = {};
        tradingPairs.forEach((pair: string) => {
          const basePrice: { [key: string]: number } = {
            'XAUUSD': 2000 + Math.random() * 100,
            'EURUSD': 1.08 + Math.random() * 0.05,
            'BTCUSD': 45000 + Math.random() * 5000,
            'GBPUSD': 1.25 + Math.random() * 0.05
          };
          newData[pair] = {
            price: basePrice[pair] ?? 0,
            change: (Math.random() - 0.5) * 2,
            volume: Math.random() * 1000000,
            signal: Math.random() > 0.7 ? (Math.random() > 0.5 ? 'BUY' : 'SELL') : 'HOLD',
            confidence: 60 + Math.random() * 40,
            mlConfidence: 50 + Math.random() * 50,
            technicalScore: Math.random() * 100,
            sentimentScore: Math.random() * 100,
            riskLevel: Math.random() > 0.7 ? 'High' : Math.random() > 0.4 ? 'Medium' : 'Low',
            lastUpdate: new Date().toISOString()
          };
        });
        setRealTimeData(newData);
        setConnectionStatus('connected');

        // Simular trades ocasionales
        if (Math.random() > 0.95) {
          const pair = tradingPairs[Math.floor(Math.random() * tradingPairs.length)];
          const signal = Math.random() > 0.5 ? 'BUY' : 'SELL';
          const price = pair ? (newData[pair]?.price ?? 0) : 0;
          const newTrade: Trade = {
            id: Date.now(),
            entryPrice: price,
            pair: pair ?? '',
            signal,
            lotSize: Math.random() * 2 + 0.01,
            confidence: 70 + Math.random() * 30,
            timestamp: new Date().toISOString(),
            status: 'OPEN',
          };
          setTrades((prev: Trade[]) => [newTrade, ...prev.slice(0, 19)]);

          // Simular cierre de trade despuÃ©s de un tiempo
          setTimeout(() => {
            const outcome = Math.random() > 0.5;
            const pnl = (Math.random() - 0.5) * 100;
            setTrades((prev: Trade[]) => prev.map(t =>
              t.id === newTrade.id
                ? { ...t, status: outcome ? 'WIN' : 'LOSS', pnl: pnl.toFixed(2) }
                : t
            ));
            setPerformance(prev => ({
              ...prev,
              totalTrades: (prev.totalTrades || 0) + 1,
              winningTrades: (prev.winningTrades || 0) + (outcome ? 1 : 0),
              totalProfit: (prev.totalProfit || 0) + pnl,
              winRate: (((prev.winningTrades || 0) + (outcome ? 1 : 0)) / ((prev.totalTrades || 0) + 1)) * 100
            }));
            const notification: Notification = {
              id: Date.now(),
              type: outcome ? 'success' : 'error',
              message: `Trade ${outcome ? 'ganador' : 'perdedor'}: ${pair} ${signal} - P&L: $${pnl.toFixed(2)}`,
              timestamp: new Date().toISOString()
            };
            setNotifications((prev: Notification[]) => [notification, ...prev.slice(0, 9)]);
          }, 5000 + Math.random() * 15000);
        }
      }, 2000);
      return () => clearInterval(interval);
    } else {
      setConnectionStatus('disconnected');
    }
  }, [isSystemRunning]);

  // Simular actualizaciÃ³n del modelo ML
  useEffect(() => {
    if (isSystemRunning) {
      const mlInterval = setInterval(() => {
        setMlModelStatus(prev => ({
          ...prev,
          trained: true,
          accuracy: Math.min(95, prev.accuracy + Math.random() * 2),
          lastTraining: 'Hace 2 horas'
        }));
      }, 10000);
      return () => clearInterval(mlInterval);
    }
  }, [isSystemRunning]);

  const handleSystemToggle = () => {
    if (!isSystemRunning) {
      setIsSystemRunning(true);
      setNotifications(prev => [{
        id: Date.now(),
        type: 'info',
        message: 'ðŸš€ Sistema de Trading IA iniciado correctamente',
        timestamp: new Date().toISOString()
      }, ...prev]);
    } else {
      setIsSystemRunning(false);
      setNotifications(prev => [{
        id: Date.now(),
        type: 'warning',
        message: 'ðŸ›‘ Sistema de Trading IA detenido',
        timestamp: new Date().toISOString()
      }, ...prev]);
      setRealTimeData({});
    }
  };
  const getStatusColor = (signal: string) => {
    switch (signal) {
      case 'BUY': return 'text-green-600 bg-green-50 border-green-200';
      case 'SELL': return 'text-red-600 bg-red-50 border-red-200';
      default: return 'text-yellow-600 bg-yellow-50 border-yellow-200';
    }
  };

  const getConfidenceColor = (confidence: number) => {
    if (confidence >= 80) return 'text-green-600';
    if (confidence >= 60) return 'text-yellow-600';
    return 'text-red-600';
  };

  const getRiskColor = (risk: string) => {
    switch (risk) {
      case 'Low': return 'text-green-600 bg-green-50';
      case 'Medium': return 'text-yellow-600 bg-yellow-50';
      case 'High': return 'text-red-600 bg-red-50';
      default: return 'text-gray-600 bg-gray-50';
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 text-white">
      {/* Header */}
      <div className="bg-slate-800/50 backdrop-blur-sm border-b border-slate-700 sticky top-0 z-50">
        <div className="max-w-7xl mx-auto px-6 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <Brain className="w-8 h-8 text-purple-400" />
              <div>
                <h1 className="text-2xl font-bold bg-gradient-to-r from-purple-400 to-pink-400 bg-clip-text text-transparent">
                  Trading IA Pro
                </h1>
                <p className="text-sm text-slate-400">Sistema Avanzado Multi-Mercado</p>
              </div>
            </div>
            
            <div className="flex items-center space-x-4">
              <div className={`flex items-center px-3 py-1 rounded-full text-sm ${
                connectionStatus === 'connected' 
                  ? 'bg-green-900/50 text-green-400' 
                  : 'bg-red-900/50 text-red-400'
              }`}>
                <div className={`w-2 h-2 rounded-full mr-2 ${
                  connectionStatus === 'connected' ? 'bg-green-400 animate-pulse' : 'bg-red-400'
                }`} />
                {connectionStatus === 'connected' ? 'MT5 Conectado' : 'MT5 Desconectado'}
              </div>
              
              <button
                onClick={handleSystemToggle}
                className={`flex items-center px-6 py-2 rounded-lg font-semibold transition-all ${
                  isSystemRunning 
                    ? 'bg-red-600 hover:bg-red-700 text-white' 
                    : 'bg-gradient-to-r from-purple-600 to-pink-600 hover:from-purple-700 hover:to-pink-700 text-white'
                }`}
              >
                {isSystemRunning ? <Pause className="w-4 h-4 mr-2" /> : <Play className="w-4 h-4 mr-2" />}
                {isSystemRunning ? 'Detener' : 'Iniciar'} Sistema
              </button>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-6 py-6 space-y-6">
        {/* MÃ©tricas Principales */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          <div className="bg-slate-800/50 backdrop-blur-sm rounded-xl p-6 border border-slate-700">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-slate-400 text-sm">Balance</p>
                <p className="text-2xl font-bold text-green-400">$25,847.32</p>
                <p className="text-xs text-slate-500">+$1,247 hoy</p>
              </div>
              <DollarSign className="w-8 h-8 text-green-400" />
            </div>
          </div>

          <div className="bg-slate-800/50 backdrop-blur-sm rounded-xl p-6 border border-slate-700">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-slate-400 text-sm">Win Rate</p>
                <p className="text-2xl font-bold text-blue-400">{(performance.winRate || 0).toFixed(1)}%</p>
                <p className="text-xs text-slate-500">{performance.totalTrades || 0} trades</p>
              </div>
              <Target className="w-8 h-8 text-blue-400" />
            </div>
          </div>

          <div className="bg-slate-800/50 backdrop-blur-sm rounded-xl p-6 border border-slate-700">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-slate-400 text-sm">P&L Total</p>
                <p className={`text-2xl font-bold ${(performance.totalProfit || 0) >= 0 ? 'text-green-400' : 'text-red-400'}`}>
                  ${(performance.totalProfit || 0).toFixed(2)}
                </p>
                <p className="text-xs text-slate-500">Ãšltimos 7 dÃ­as</p>
              </div>
              <BarChart3 className="w-8 h-8 text-purple-400" />
            </div>
          </div>

          <div className="bg-slate-800/50 backdrop-blur-sm rounded-xl p-6 border border-slate-700">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-slate-400 text-sm">Modelo IA</p>
                <p className={`text-2xl font-bold ${mlModelStatus.trained ? 'text-green-400' : 'text-red-400'}`}>
                  {mlModelStatus.accuracy?.toFixed(1) || 0}%
                </p>
                <p className="text-xs text-slate-500">
                  {mlModelStatus.trained ? 'Entrenado' : 'Sin entrenar'}
                </p>
              </div>
              <Brain className="w-8 h-8 text-purple-400" />
            </div>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Panel de Pares de Divisas */}
          <div className="lg:col-span-2 space-y-6">
            <div className="bg-slate-800/50 backdrop-blur-sm rounded-xl p-6 border border-slate-700">
              <h3 className="text-xl font-bold mb-6 flex items-center">
                <Activity className="w-6 h-6 text-green-400 mr-2" />
                AnÃ¡lisis en Tiempo Real
              </h3>
              <div className="space-y-4">
                {tradingPairs.map(pair => {
                  const data = realTimeData[pair];
                  return (
                    <div key={pair} className="bg-slate-700/50 rounded-lg p-4 border border-slate-600">
                      <div className="flex items-center justify-between mb-3">
                        <h4 className="text-lg font-bold text-purple-400">{pair}</h4>
                        {data && (
                          <span className={`px-3 py-1 rounded-full text-sm font-semibold border ${getStatusColor(data.signal)}`}>
                            {data.signal}
                          </span>
                        )}
                      </div>
                      {data ? (
                        <div className="space-y-2">
                          <div className="flex justify-between items-center">
                            <span className="text-slate-400">Precio:</span>
                            <div className="text-right">
                              <span className="font-mono text-white">
                                {data.price.toFixed(pair === 'XAUUSD' || pair === 'BTCUSD' ? 2 : 5)}
                              </span>
                              <span className={`ml-2 text-sm ${data.change >= 0 ? 'text-green-400' : 'text-red-400'}`}>
                                {data.change >= 0 ? '+' : ''}{data.change.toFixed(2)}%
                              </span>
                            </div>
                          </div>
                          <div className="flex justify-between">
                            <span className="text-slate-400">Confianza IA:</span>
                            <span className={`font-bold ${getConfidenceColor(data.confidence)}`}>{data.confidence.toFixed(1)}%</span>
                          </div>
                          <div className="flex justify-between">
                            <span className="text-slate-400">ML Score:</span>
                            <span className={`font-semibold ${getConfidenceColor(data.mlConfidence)}`}>{data.mlConfidence.toFixed(1)}%</span>
                          </div>
                          <div className="flex justify-between">
                            <span className="text-slate-400">Riesgo:</span>
                            <span className={`px-2 py-1 rounded text-xs font-semibold ${getRiskColor(data.riskLevel)}`}>{data.riskLevel}</span>
                          </div>
                          <div className="mt-3 pt-3 border-t border-slate-600">
                            <div className="flex justify-between text-sm">
                              <span className="text-slate-400">TÃ©cnico:</span>
                              <span>{data.technicalScore.toFixed(0)}%</span>
                            </div>
                            <div className="flex justify-between text-sm">
                              <span className="text-slate-400">Sentiment:</span>
                              <span>{data.sentimentScore.toFixed(0)}%</span>
                            </div>
                          </div>
                        </div>
                      ) : (
                        <div className="flex items-center justify-center h-32">
                          <div className="text-center">
                            <Eye className="w-8 h-8 text-slate-500 mx-auto mb-2" />
                            <p className="text-slate-500">Esperando datos...</p>
                          </div>
                        </div>
                      )}
                    </div>
                  );
                })}
              </div>
            </div>

            {/* Historial de Trades */}
            <div className="bg-slate-800/50 backdrop-blur-sm rounded-xl p-6 border border-slate-700">
              <h3 className="text-xl font-bold mb-4 flex items-center">
                <TrendingUp className="w-6 h-6 text-green-400 mr-2" />
                Trades Recientes
              </h3>
              <div className="space-y-3 max-h-80 overflow-y-auto">
                {trades.length > 0 ? trades.map(trade => (
                  <div key={trade.id} className="bg-slate-700/50 rounded-lg p-4 border border-slate-600">
                    <div className="flex items-center justify-between mb-2">
                      <div className="flex items-center space-x-3">
                        <span className="font-semibold text-purple-400">{trade.pair}</span>
                        <span className={`px-2 py-1 rounded text-xs font-semibold border ${getStatusColor(trade.signal)}`}>{trade.signal}</span>
                        {trade.status !== 'OPEN' && (
                          <span className={`px-2 py-1 rounded text-xs font-semibold ${trade.status === 'WIN' ? 'text-green-600 bg-green-50 border border-green-200' : 'text-red-600 bg-red-50 border border-red-200'}`}>{trade.status}</span>
                        )}
                      </div>
                      <span className="text-sm text-slate-400">{new Date(trade.timestamp).toLocaleTimeString()}</span>
                    </div>
                    <div className="grid grid-cols-2 gap-4 text-sm">
                      <div>
                        <span className="text-slate-400">Entry: </span>
                        <span className="font-mono">{trade.entryPrice.toFixed(trade.pair === 'XAUUSD' || trade.pair === 'BTCUSD' ? 2 : 5)}</span>
                      </div>
                      <div>
                        <span className="text-slate-400">Lote: </span>
                        <span>{trade.lotSize.toFixed(2)}</span>
                      </div>
                      <div>
                        <span className="text-slate-400">Confianza: </span>
                        <span className={getConfidenceColor(trade.confidence)}>{trade.confidence.toFixed(1)}%</span>
                      </div>
                      {trade.pnl && (
                        <div>
                          <span className="text-slate-400">P&L: </span>
                          <span className={`font-semibold ${parseFloat(trade.pnl) >= 0 ? 'text-green-400' : 'text-red-400'}`}>${trade.pnl}</span>
                        </div>
                      )}
                    </div>
                  </div>
                )) : (
                  <div className="text-center py-8">
                    <BarChart3 className="w-12 h-12 text-slate-500 mx-auto mb-3" />
                    <p className="text-slate-500">No hay trades recientes</p>
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* Panel Lateral */}
          <div className="space-y-6">
            {/* Notificaciones */}
            <div className="bg-slate-800/50 backdrop-blur-sm rounded-xl p-6 border border-slate-700">
              <h3 className="text-lg font-bold mb-4 flex items-center">
                <Bell className="w-5 h-5 text-yellow-400 mr-2" />
                Notificaciones
              </h3>
              <div className="space-y-3 max-h-64 overflow-y-auto">
                {notifications.length > 0 ? notifications.map(notification => (
                  <div key={notification.id} className={`p-3 rounded-lg border-l-4 ${
                    notification.type === 'success' ? 'bg-green-900/20 border-green-400' :
                    notification.type === 'error' ? 'bg-red-900/20 border-red-400' :
                    notification.type === 'warning' ? 'bg-yellow-900/20 border-yellow-400' :
                    'bg-blue-900/20 border-blue-400'
                  }`}>
                    <p className="text-sm">{notification.message}</p>
                    <p className="text-xs text-slate-500 mt-1">{new Date(notification.timestamp).toLocaleTimeString()}</p>
                  </div>
                )) : (
                  <div className="text-center py-4">
                    <Bell className="w-8 h-8 text-slate-500 mx-auto mb-2" />
                    <p className="text-slate-500 text-sm">Sin notificaciones</p>
                  </div>
                )}
              </div>
            </div>

            {/* Estado del Sistema */}
            <div className="bg-slate-800/50 backdrop-blur-sm rounded-xl p-6 border border-slate-700">
              <h3 className="text-lg font-bold mb-4 flex items-center">
                <Settings className="w-5 h-5 text-purple-400 mr-2" />
                Estado del Sistema
              </h3>
              
              <div className="space-y-3">
                <div className="flex justify-between items-center">
                  <span className="text-slate-400">Sistema:</span>
                  <span className={`px-2 py-1 rounded text-xs font-semibold ${
                    isSystemRunning ? 'bg-green-900/50 text-green-400' : 'bg-red-900/50 text-red-400'
                  }`}>
                    {isSystemRunning ? 'ACTIVO' : 'DETENIDO'}
                  </span>
                </div>
                
                <div className="flex justify-between items-center">
                  <span className="text-slate-400">Trades Hoy:</span>
                  <span className="text-white">{performance.totalTrades || 0}</span>
                </div>
                
                <div className="flex justify-between items-center">
                  <span className="text-slate-400">Modelo ML:</span>
                  <span className={`px-2 py-1 rounded text-xs font-semibold ${
                    mlModelStatus.trained ? 'bg-green-900/50 text-green-400' : 'bg-red-900/50 text-red-400'
                  }`}>
                    {mlModelStatus.trained ? 'ENTRENADO' : 'NO ENTRENADO'}
                  </span>
                </div>
                
                <div className="flex justify-between items-center">
                  <span className="text-slate-400">Riesgo/Trade:</span>
                  <span className="text-white">{settings.riskPerTrade}%</span>
                </div>
                
                <div className="flex justify-between items-center">
                  <span className="text-slate-400">Min. Confianza:</span>
                  <span className="text-white">{settings.minConfidence}%</span>
                </div>
              </div>
            </div>

            {/* ConfiguraciÃ³n RÃ¡pida */}
            <div className="bg-slate-800/50 backdrop-blur-sm rounded-xl p-6 border border-slate-700">
              <h3 className="text-lg font-bold mb-4 flex items-center">
                <RotateCcw className="w-5 h-5 text-blue-400 mr-2" />
                ConfiguraciÃ³n
              </h3>
              
              <div className="space-y-4">
                <div>
                  <label className="block text-sm text-slate-400 mb-1">Riesgo por Trade (%)</label>
                  <input
                    type="number"
                    min="0.1"
                    step="0.1"
                    value={settings.riskPerTrade}
                    onChange={(e) => setSettings(prev => ({...prev, riskPerTrade: parseFloat(e.target.value)}))}
                    className="w-full px-3 py-2 bg-slate-700 border border-slate-600 rounded-lg text-white focus:border-purple-400 focus:outline-none"
                  />
                </div>
                
                <div>
                  <label className="block text-sm text-slate-400 mb-1">Confianza MÃ­nima (%)</label>
                  <input
                    type="number"
                    min="50"
                    step="5"
                    value={settings.minConfidence}
                    onChange={(e) => setSettings(prev => ({...prev, minConfidence: parseInt(e.target.value)}))}
                    className="w-full px-3 py-2 bg-slate-700 border border-slate-600 rounded-lg text-white focus:border-purple-400 focus:outline-none"
                  />
                </div>
                
                <div>
                  <label className="block text-sm text-slate-400 mb-1">Trades MÃ¡x/DÃ­a</label>
                  <input
                    type="number"
                    max={20}
                    value={settings.maxDailyTrades}
                    onChange={(e) => setSettings(prev => ({...prev, maxDailyTrades: parseInt(e.target.value)}))}
                    className="w-full px-3 py-2 bg-slate-700 border border-slate-600 rounded-lg text-white focus:border-purple-400 focus:outline-none"
                  />
                </div>
                <button
                  className="w-full mt-4 px-4 py-2 bg-purple-600 hover:bg-purple-700 rounded-lg font-semibold"
                  onClick={() => {
                    setNotifications(prev => [{
                      id: Date.now(),
                      type: 'info',
                      message: 'âš™ï¸ ConfiguraciÃ³n actualizada',
                      timestamp: new Date().toISOString()
                    }, ...prev]);
                  }}
                >
                  Aplicar Cambios
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default TradingDashboard;
